import {
  useAddFavoriteMutation,
  useGetReservationMutation,
} from '@api/requestApi'
import HeadInfo from '@components/HeadInfo'
import CloseIcon from '@mui/icons-material/Close'
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown'
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp'
import { Box, MenuItem, Select } from '@mui/material'
import { styled } from '@mui/material/styles'
import en from '@public/locales/en/productDetails.json'
import productsEnglish from '@public/locales/en/products.json'
import ko from '@public/locales/ko/productDetails.json'
import productsKorean from '@public/locales/ko/products.json'
import { reservation, reset } from '@store/reservationDetailSlice'
import axios from 'axios'
import { useRouter } from 'next/router'
import { useEffect, useState } from 'react'
import { CopyToClipboard } from 'react-copy-to-clipboard'
import { useDispatch } from 'react-redux'

import {
  FacebookIcon,
  FacebookShareButton,
  LineIcon,
  LineShareButton,
  TwitterIcon,
  TwitterShareButton,
} from 'react-share'

type data = {
  ages: string
  airport: string
  basicPrice: number
  companion: string
  continent: string
  country: string
  genderGroup: string
  id: number
  info: string
  points: string
  productName: string
  productOptionList: {
    출발일: []
  }
  region: string
  religion: string
  theme: string
  thumbnail: string
}
type productOptionList = {
  additional: number
  id: number
  name: string
  required: boolean
  value: string
}
type paramType = {
  params: {
    id: string
  }
}

export default function productId(data: data) {
  const router = useRouter()

  const { locale } = router
  const translate = locale === 'en' ? en : ko
  const translateProducts = locale === 'en' ? productsEnglish : productsKorean

  const departure = data.productOptionList.출발일

  const [selectDeperatureValue, setSelectDeperatureValue] =
    useState<string>('none')

  const dispatch = useDispatch()
  const [getReservation]: any = useGetReservationMutation()

  const moveBook = async () => {
    const accessToken = localStorage.getItem('accessToken')
    if (!accessToken) {
      setNeedLoginModalIsOpenIsOpen(!needLoginModalIsOpen)
    } else {
      const res = await getReservation({
        accessToken: accessToken,
      })
      const reservationData = res.data.data.filter(
        (resData: any) => resData.product_id === data.id,
      )
      if (
        accessToken &&
        selectDeperatureValue !== 'none' &&
        !reservationData.length
      ) {
        dispatch(reset())
        dispatch(
          reservation({
            productId: data.id,
            productName: data.productName,
            airport: data.airport,
            productOptionList: selectDeperatureValue,
            basicPrice: data.basicPrice,
            thumbnail: data.thumbnail,
          }),
        )
        router.push('/book')
      } else if (
        selectDeperatureValue === '' ||
        selectDeperatureValue === 'none'
      ) {
        setNeedDeperatureModalIsOpen(!needDeperatureModalIsOpen)
      } else if (reservationData.length) {
        setExistReservationModalIsOpen(!existResevationModalIsOpen)
      } else {
        setNeedLoginModalIsOpenIsOpen(!needLoginModalIsOpen)
      }
    }
  }

  const [shareModalIsOpen, setShareModalIsOpen] = useState<boolean>(false)
  const [favoriteModalIsOpen, setFavoriteModalIsOpen] = useState<boolean>(false)
  const [favoriteDuplicateModalIsOpen, setFavoriteDuplicateModalIsOpen] =
    useState<boolean>(false)
  const [needLoginModalIsOpen, setNeedLoginModalIsOpenIsOpen] =
    useState<boolean>(false)
  const [needDeperatureModalIsOpen, setNeedDeperatureModalIsOpen] =
    useState<boolean>(false)
  const [existResevationModalIsOpen, setExistReservationModalIsOpen] =
    useState<boolean>(false)
  const [addFavorite]: any = useAddFavoriteMutation()

  const openFavorite = async () => {
    const accessToken = localStorage.getItem('accessToken')
    try {
      if (accessToken) {
        const res = await addFavorite({
          data: { product_id: data.id },
          accessToken: accessToken,
        })
        if (res.data.statusCode == 400) {
          setFavoriteDuplicateModalIsOpen(!favoriteDuplicateModalIsOpen)
        } else setFavoriteModalIsOpen(!favoriteModalIsOpen)
      } else {
        setNeedLoginModalIsOpenIsOpen(!needLoginModalIsOpen)
      }
    } catch (e) {
      console.log(e)
    }
  }

  const closeFavoriteModal = () => {
    setFavoriteModalIsOpen(!favoriteModalIsOpen)
  }
  const closeDuplicateFavoriteModal = () => {
    setFavoriteDuplicateModalIsOpen(!favoriteDuplicateModalIsOpen)
  }
  const closeNeedLoginModal = () => {
    setNeedLoginModalIsOpenIsOpen(!needLoginModalIsOpen)
  }
  const closeExistResevationModalIsOpen = () => {
    setExistReservationModalIsOpen(!existResevationModalIsOpen)
  }
  const closeNeedDeperatureModal = () => {
    setNeedDeperatureModalIsOpen(!needDeperatureModalIsOpen)
  }

  const selectDeperature = (e: any) => {
    setSelectDeperatureValue(e.target.value)
    if (e.target.value === 'none') {
      dispatch(reservation({ productOptionList: '' }))
    } else dispatch(reservation({ productOptionList: e.target.value }))
  }

  const moveFavorite = () => {
    router.push('/saved')
  }

  const moveLogin = () => {
    router.push('/signin')
  }

  let currentUrl = ''
  if (typeof window !== 'undefined') {
    currentUrl = window.location.href

    if (!window.Kakao.isInitialized()) {
      window.Kakao.init(process.env.NEXT_PUBLIC_KAKAO_URL)
    }
  }

  const shareKakao = () => {
    const { Kakao, location } = window
    Kakao.Link.sendScrap({
      requestUrl: location.href,
    })
  }

  const shareKakaoStory = () => {
    window.Kakao.Story.share({
      url: encodeURI(encodeURIComponent(currentUrl)),
      text: '공유할 상품',
    })
  }

  const shareNaver = () => {
    let url = encodeURI(encodeURIComponent(currentUrl))
    let title = encodeURI('공유할 상품')
    let shareURL =
      'https://share.naver.com/web/shareView?url=' + url + '&title=' + title
    document.location = shareURL
  }

  const shareNaverBand = () => {
    let url = encodeURI(encodeURIComponent(currentUrl))
    let title = encodeURI('공유할 상품')
    let shareURL = 'https://band.us/plugin/share?body' + url + '&route=' + title
    document.location = shareURL
  }
  const closeShareModal = () => {
    setShareModalIsOpen(!shareModalIsOpen)
  }
  const detailData = JSON.parse(data.info)

  const [showMore, setShowMore] = useState<boolean>(false)
  const [visible, setVisible] = useState<string>('hidden')
  const [maxHeight, setMaxHeight] = useState<string>('100vw')

  useEffect(() => {
    localStorage.getItem('accessToken')

    if (showMore) {
      setVisible('visible')
      setMaxHeight('100%')
    } else {
      setVisible('hidden')
      setMaxHeight('100vw')
    }
  }, [showMore, visible, maxHeight])

  const StyledSection = styled('div')(() => ({
    backgroundColor: '#fff',
    padding: '1.6rem',
    marginBottom: '1.6rem',
  }))

  return (
    <div className="productDetail">
      <HeadInfo
        title={
          translate['페이지 제목1'] +
          translateProducts[data.productName] +
          translate['페이지 제목2']
        }
      />
      <div className="thumbnail">
        <img src={data.thumbnail} alt="img" className="img" />
      </div>

      <div className="basicInformation">
        {data.country.split(',')[1] ? (
          <div className="nations">
            <span className="nation1">
              {translateProducts[data.country].split(',')[0]}
            </span>
            <span className="nation2">
              {translateProducts[data.country].split(', ')[1]}
            </span>
          </div>
        ) : (
          <span className="nation">{translateProducts[data.country]}</span>
        )}
        <div className="title">{translateProducts[data.productName]}</div>
        <div className="hashTags">
          <div className="hashTag">#{translateProducts[data.ages]} &nbsp;</div>
          <div className="hashTag">
            {data.theme !== '상관 없음' && '#' + translateProducts[data.theme]}
            &nbsp;
          </div>
        </div>
        {data.basicPrice == 0 ? (
          <div className="price">{translate['가격 문의']}</div>
        ) : (
          <div className="price">
            {data.basicPrice.toLocaleString('ko-KR')} {translate['원']}
          </div>
        )}
      </div>
      <div className="line"></div>

      <footer className="wish_reser">
        <div
          className="wish"
          onClick={() => {
            openFavorite()
          }}
        >
          {translate['찜하기']}
        </div>
        {favoriteModalIsOpen && (
          <div className="favoriteContainer" onClick={closeFavoriteModal}>
            <div
              className="favoriteModalBody"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="contents">
                {translate['찜 성공! 상품 목록을 확인하시겠습니까?']}
              </div>
              <div className="select">
                <div className="goFavorite" onClick={moveFavorite}>
                  {translate['찜 목록 보기']}
                </div>
                <div className="stay" onClick={closeFavoriteModal}>
                  {translate['쇼핑 계속하기']}
                </div>
              </div>
            </div>
          </div>
        )}
        {favoriteDuplicateModalIsOpen && (
          <div
            className="favoriteContainer"
            onClick={closeDuplicateFavoriteModal}
          >
            <div
              className="favoriteModalBody"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="contents">
                {translate['이미 찜한 상품입니다!']}
              </div>
              <div className="select">
                <div className="goFavorite" onClick={moveFavorite}>
                  {translate['찜 목록 보기']}
                </div>
                <div className="stay" onClick={closeDuplicateFavoriteModal}>
                  {translate['쇼핑 계속하기']}
                </div>
              </div>
            </div>
          </div>
        )}
        {needLoginModalIsOpen && (
          <div className="needLoginContainer" onClick={closeNeedLoginModal}>
            <div
              className="needLoginModalBody"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="contents">
                {translate['로그인이 필요한 서비스입니다!']}
              </div>
              <div className="select">
                <div className="goLogin" onClick={moveLogin}>
                  {translate['로그인']}
                </div>
                <div className="stay" onClick={closeNeedLoginModal}>
                  {translate['쇼핑 계속하기']}
                </div>
              </div>
            </div>
          </div>
        )}
        {existResevationModalIsOpen && (
          <div
            className="needDeperatureContainer"
            onClick={closeExistResevationModalIsOpen}
          >
            <div
              className="needDeperatureModalBody"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="contents">
                {translate['이미 예약된 상품입니다!']}
              </div>
              <div className="select">
                <div className="ok" onClick={closeExistResevationModalIsOpen}>
                  {translate['확인']}
                </div>
              </div>
            </div>
          </div>
        )}
        {needDeperatureModalIsOpen && (
          <div
            className="needDeperatureContainer"
            onClick={closeNeedDeperatureModal}
          >
            <div
              className="needDeperatureModalBody"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="contents">
                {translate['출발일을 선택해주세요!']}
              </div>
              <div className="select">
                <div className="ok" onClick={closeNeedDeperatureModal}>
                  {translate['확인']}
                </div>
              </div>
            </div>
          </div>
        )}
        <div
          className="reservation"
          onClick={() => {
            moveBook()
          }}
        >
          {translate['예약하기']}
        </div>
      </footer>

      <div className="departureDate">
        <div className="departure">{translate['출발일 (필수)']}</div>
        <StyledSection className="selectBoxMui">
          <Box>
            <Select
              fullWidth
              size="small"
              value={selectDeperatureValue}
              onChange={selectDeperature}
              sx={{ '& legend': { display: 'none' }, '& fieldset': { top: 0 } }}
            >
              <MenuItem value="none">{translate['출발일 선택하기']}</MenuItem>
              {departure &&
                departure.map((option: productOptionList) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.value}
                  </MenuItem>
                ))}
            </Select>
          </Box>
        </StyledSection>
      </div>
      <div className="nextArea" />

      <div className="inforTitle">{translate['여행 정보']}</div>
      <div className="line"></div>
      <div className="titleContent">
        <div className="title">{translate['지역']}</div>
        <div className="content">{data.region}</div>
      </div>
      <div className="titleContent">
        <div className="title">{translate['특징']}</div>
        <div className="content">{data.points}</div>
      </div>
      <div className="titleContent">
        <div className="title">{translate['항공']}</div>
        <div className="content">{data.airport}</div>
      </div>
      <div className="share" onClick={() => setShareModalIsOpen(true)}>
        {translate['공유하기']}
      </div>
      {shareModalIsOpen && (
        <div className="shareContainer" onClick={closeShareModal}>
          <div className="shareModalBody" onClick={(e) => e.stopPropagation()}>
            <div className="top">
              <div className="sharePhrases">{translate['공유하기']}</div>
              <CloseIcon
                fontSize="medium"
                className="closeButton"
                onClick={closeShareModal}
              />
            </div>
            <div className="middle">
              <div className="FlexContainer">
                <div className="GridContainer">
                  <div className="site">
                    <img
                      src="https://new-version.download/wp-content/uploads/%EC%B9%B4%EC%B9%B4%EC%98%A4%ED%86%A1-PC%EB%B2%84%EC%A0%84-150x150.png"
                      alt="kakaotalk"
                      className="kakaoImg"
                      onClick={shareKakao}
                    />
                    <div className="kakao">{translate['카카오톡']}</div>
                  </div>
                  <div className="site">
                    <img
                      src="https://developers.kakao.com/sdk/js/resources/story/icon_small.png"
                      alt="kakaotalk"
                      className="kakaoImg"
                      onClick={shareKakaoStory}
                    />
                    <div className="kakao">{translate['카카오스토리']}</div>
                  </div>
                  <div className="site">
                    <LineShareButton url={currentUrl}>
                      <LineIcon
                        size={48}
                        round={true}
                        borderRadius={24}
                        className="iconSize"
                      />
                    </LineShareButton>
                    <div className="siteName">{translate['라인']}</div>
                  </div>
                  <div className="site">
                    <img
                      src="data:image/png;base64,"
                      alt="naver"
                      className="naverBandImg"
                      onClick={shareNaverBand}
                    />
                    <div className="naverBand">{translate['밴드']}</div>
                  </div>
                  <div className="site">
                    <img
                      src="https://clova-phinf.pstatic.net/MjAxODAzMjlfOTIg/MDAxNTIyMjg3MzM3OTAy.WkiZikYhauL1hnpLWmCUBJvKjr6xnkmzP99rZPFXVwgg.mNH66A47eL0Mf8G34mPlwBFKP0nZBf2ZJn5D4Rvs8Vwg.PNG/image.png"
                      alt="naver"
                      className="naverImg"
                      onClick={shareNaver}
                    />
                    <div className="naver">{translate['네이버']}</div>
                  </div>

                  <div className="site">
                    <FacebookShareButton url={currentUrl}>
                      <FacebookIcon
                        size={48}
                        round={true}
                        borderRadius={24}
                        className="iconSize"
                      />
                    </FacebookShareButton>
                    <div className="siteName">{translate['페이스북']}</div>
                  </div>
                  <div className="site">
                    <TwitterShareButton url={currentUrl}>
                      <TwitterIcon
                        size={48}
                        round={true}
                        borderRadius={24}
                        className="iconSize"
                      ></TwitterIcon>
                    </TwitterShareButton>
                    <div className="siteName">{translate['트위터']}</div>
                  </div>
                </div>
              </div>
            </div>
            <div className="bottom">
              <input className="input" value={currentUrl} disabled />
              <CopyToClipboard text={currentUrl}>
                <button className="button">{translate['복사']}</button>
              </CopyToClipboard>
            </div>
          </div>
        </div>
      )}
      <div className="nextArea" />

      <div className="inforTitle">{translate['여행 소개']}</div>
      <div
        style={{
          overflow: `${visible}`,
          maxHeight: `${maxHeight}`,
          marginBottom: '1rem',
          width: '100%',
        }}
      >
        <div className="explicate">
          {detailData &&
            detailData.map((data: any, index: number) => (
              <div key={index}>
                {data.type === 'image' ? (
                  <img src={data.url} alt="img" width="100%" />
                ) : (
                  <></>
                )}
              </div>
            ))}
        </div>
      </div>
      {showMore ? (
        <div className="more" onClick={() => setShowMore(!showMore)}>
          <div className="pharse">{translate['접기']}</div>
          <KeyboardArrowUpIcon fontSize="small" style={{ color: '#4581f8' }} />
        </div>
      ) : (
        <div className="more" onClick={() => setShowMore(!showMore)}>
          <div className="pharse">{translate['더보기']}</div>
          <KeyboardArrowDownIcon
            fontSize="small"
            style={{ color: '#4581f8' }}
          />
        </div>
      )}
      <div className="introNextArea" />
    </div>
  )
}

export const getServerSideProps = async (context: paramType) => {
  const res = await axios.get(
    process.env.NEXT_PUBLIC_API_URL + `/product-details/${context.params.id}`,
  )
  const data = await res.data.data
  return { props: data }
}
